<script setup lang="ts">

import { TeamOutlined, SettingOutlined } from '@ant-design/icons-vue'

import SmallButton from './SmallButton.vue';
import MyProfile from './MyProfile.vue';
import { useRouter } from 'vue-router';
import { Dropdown, Menu, MenuItem } from 'ant-design-vue';
import { useHallState } from '@/stores/hall';
import { getUserId } from '@/utils/auth';

import { HallStatus } from '@/core/game/hall';
import { computed, ref, watch } from 'vue';

import { pageId } from '@/utils/pageis';
import { timeIntervalStr } from '@/utils/timeUtils'
import { useMyProfile } from '@/stores/my';
import { useRoomState } from '@/stores/room';
import { useSoundFX } from '@/stores/soundfx';

const router = useRouter()
const roomInfo = useRoomState()

const soundfx = useSoundFX()

const trySwitchAndSound = (path: string) => {
    if (router.currentRoute.value.path != path) {
        soundfx.play('topbar-switch')
        router.push(path)
    }
}

const mainClick = () => {
    if (pageId.value != 'in-match' && hall.hallStatus == HallStatus.IN_MATCH) {
        trySwitchAndSound('/contest')
    } else if (pageId.value != 'in-room' && hall.hallStatus == HallStatus.IN_ROOM) {
        trySwitchAndSound(`/roomDetails/${roomInfo.roomInfo?.roomid}`)
    } else {
        trySwitchAndSound('/')
    }
}

const click = (bid: 1 | 2) => {
    if (bid == 1) {
        trySwitchAndSound('/history')
    } else if (bid == 2) {
        trySwitchAndSound('/rooms')
    }
}

const hover = () => {
    soundfx.play('button-hover')
}

const hall = useHallState()
const myProfile = useMyProfile()

const log_out = () => {

  hall.hall.logout()
  localStorage.removeItem('token')
  localStorage.removeItem('userId')
  
  myProfile.userId = ''
  router.push('/auth/login')

}

const thisId = getUserId();

const pushProfile = () => {
  router.push(`/user/profile/${thisId}`);
}

const matchingActive = computed(()=>hall.hallStatus == HallStatus.MATCHING ? undefined : {opacity: 0})
const settlingActive = computed(()=>hall.hallStatus == HallStatus.SETTLING ? undefined : {opacity: 0})
const logoActive = computed(()=>
        (hall.hallStatus == HallStatus.IN_MATCH && pageId.value == 'in-match')
    ||  (hall.hallStatus == HallStatus.IN_ROOM && pageId.value == 'in-room')
    ? undefined : {opacity: 0})
const logoBackActive = computed(()=>hall.hallStatus == HallStatus.IN_MATCH && pageId.value != 'in-match' ? undefined : {opacity: 0})
const logoBackActiveLogo = computed(()=>
        hall.hallStatus == HallStatus.IN_MATCH && pageId.value != 'in-match' 
    ||  hall.hallStatus == HallStatus.IN_ROOM && pageId.value != 'in-room' 
    ? undefined : {opacity: 0})
const logoBackRoomActive = computed(()=>hall.hallStatus == HallStatus.IN_ROOM && pageId.value != 'in-room' ? undefined : {opacity: 0})
const defaultActive = computed(()=>hall.hallStatus == HallStatus.ONLINE ? undefined : {opacity: 0})
const buttonActive = computed(()=>hall.hallStatus == HallStatus.OFFLINE ? 'disabled' : undefined)
const downerActive = computed(()=>hall.hallStatus == HallStatus.MATCHING ? undefined : 'disabled')

const buttonActiveMain = computed(()=>pageId.value == 'home' || hall.hallStatus == HallStatus.MATCHING ? 'active' : undefined)
const buttonActive1 = computed(()=>pageId.value == 'history' ? 'active' : undefined)
const buttonActive2 = computed(()=>pageId.value == 'rooms' ? 'active' : undefined)


const matchingTime = ref<string>("00:00")

const startMatchingTimer = () => {
    const startTime = Date.now()    
    const intervalFunc = () => {
        if (hall.hallStatus != HallStatus.MATCHING) {
            clearInterval(interval)
            return
        }

        const newTime = Date.now()

        matchingTime.value = timeIntervalStr(startTime, newTime)
    }

    const interval: ReturnType<typeof setInterval> = setInterval(intervalFunc, 1000)
}

watch(downerActive, (val) => {
    if (val === undefined) {
        // 即正在匹配
        startMatchingTimer()
    }
})
//39.72 282.42
const cancelMatch = () => {
    router.go(0)
}

</script>

